```c++
class Solution {
public:
    int maxScoreSightseeingPair(vector<int>& A) {
        int res = 0;
        int max_i = A[0];

        for (int i = 1; i < A.size(); i++) {
            res = max(res, max_i + A[i] - i);
            max_i = max(max_i, A[i]+i);
        }
        return res;
    }
};
