class Solution {
public:
    int triangleNumber(vector<int>& nums) {
        if (nums.size() < 3) return 0;

        int res = 0;
        sort(nums.begin(), nums.end());
        for (int i = nums.size()-1; i > 1; i--) {
            int l = 0, r = i-1;
            while (l < r) {
