class Solution {
public:
    int numSubarrayBoundedMax(vector<int>& A, int L, int R) {
        int res = 0;
        int l = 0;
        int r = 0;

        for (int i = 0; i < A.size(); i++) {
            if (A[i] >= L && A[i] <= R) {
                res += i-l+1;
                r = i+1;
            } else if (A[i] < L) {
                res += max(0, r-l);
